import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Card, CardContent, CardMedia, Grid, Box, IconButton, Avatar, Dialog, AppBar, Toolbar, Autocomplete } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import axios from 'axios';
import CentralizaBox from '../componentes/centralizar.jsx';

const Admin = () => {
  const [filmes, setFilmes] = useState([]);
  const [novoFilme, setNovoFilme] = useState({
    cartaz: '',
    titulo: '',
    descricao: '',
    categoria: '',
    elenco: ''
  });
  const [open, setOpen] = useState(false);
  const [searchValue, setSearchValue] = useState('');
  const [filteredFilmes, setFilteredFilmes] = useState([]);

  useEffect(() => {
    const fetchFilmes = async () => {
      try {
        const response = await axios.get('http://localhost:4000/filmes');
        setFilmes(response.data);
      } catch (error) {
        console.error('Erro ao buscar filmes:', error);
      }
    };
    fetchFilmes();
  }, []);

  useEffect(() => {
    setFilteredFilmes(
      filmes.filter(filme => 
        filme.titulo.toLowerCase().includes(searchValue.toLowerCase())
      )
    );
  }, [searchValue, filmes]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNovoFilme(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleAddFilme = async () => {
    try {
      const response = await axios.post('http://localhost:4000/filmes', novoFilme);
      if (response.data.success) {
        setFilmes([...filmes, response.data.filme]);
        setNovoFilme({ cartaz: '', titulo: '', descricao: '', categoria: '', elenco: '' });
        setOpen(false);
      } else {
        alert('Erro ao adicionar filme');
      }
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
    }
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const nome = localStorage.getItem('nome');
  const avatarLetter = nome ? nome.charAt(0).toUpperCase() : '';

  return (
    <Box sx={{ width: '95%', margin: 'auto', mt: 2 }}>
      <AppBar position="static" sx={{ mb: 2 }}>
        <Toolbar>
          <Box component="img" src="https://upload.wikimedia.org/wikipedia/commons/thumb/0/08/Netflix_2015_logo.svg/320px-Netflix_2015_logo.svg.png" alt="Netflix Logo" sx={{ height: 50, mr: 2 }} />
            renderInput={(params) => (
              <TextField
                {...params}
                label="Pesquisar"
                variant="outlined"
                onChange={(e) => setSearchValue(e.target.value)}
                sx={{ flexGrow: 1, mr: 2 }}
              />
            )}
          <IconButton color="inherit" onClick={handleClickOpen}>
            <AddIcon />
          </IconButton>
          <Avatar sx={{ ml: 2 }}>{avatarLetter}</Avatar>
        </Toolbar>
      </AppBar>
      <Grid container spacing={2}>
        {filteredFilmes.map(filme => (
          <Grid item xs={12} sm={6} md={4} key={filme.id}>
            <Card>
              <CardMedia
                component="img"
                height="555"
                image={filme.cartaz}
                alt={filme.titulo}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {filme.titulo}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {filme.descricao}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  <strong>Categoria:</strong> {filme.categoria}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  <strong>Elenco:</strong> {filme.elenco}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Dialog open={open} onClose={handleClose} fullWidth>
        <CentralizaBox>
          <Typography variant="h4" gutterBottom>
            Adicionar Filme
          </Typography>
          <form noValidate autoComplete="off">
            <TextField
              label="Cartaz"
              name="cartaz"
              value={novoFilme.cartaz}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Título"
              name="titulo"
              value={novoFilme.titulo}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Descrição"
              name="descricao"
              value={novoFilme.descricao}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Categoria"
              name="categoria"
              value={novoFilme.categoria}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Elenco"
              name="elenco"
              value={novoFilme.elenco}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <Button variant="contained" color="primary" onClick={handleAddFilme} fullWidth>
              Adicionar Filme
            </Button>
          </form>
        </CentralizaBox>
      </Dialog>
    </Box>
  );
};

export default Admin;
