import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Card, CardContent, CardMedia, Grid } from '@mui/material';
import axios from 'axios';
import CentralizaBox from '../componentes/centralizar.jsx';

const Admin = () => {
  const [filmes, setFilmes] = useState([]);
  const [novoFilme, setNovoFilme] = useState({
    cartaz: '',
    titulo: '',
    descricao: '',
    categoria: '',
    elenco: ''
  });

  useEffect(() => {
    const fetchFilmes = async () => {
      try {
        const response = await axios.get('http://localhost:4000/filmes');
        setFilmes(response.data);
      } catch (error) {
        console.error('Erro ao buscar filmes:', error);
      }
    };
    fetchFilmes();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNovoFilme(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleAddFilme = async () => {
    try {
      const response = await axios.post('http://localhost:4000/filmes', novoFilme);
      if (response.data.success) {
        setFilmes([...filmes, response.data.filme]);
        setNovoFilme({ cartaz: '', titulo: '', descricao: '', categoria: '', elenco: '' });
      } else {
        alert('Erro ao adicionar filme');
      }
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
    }
  };

  return (
    <CentralizaBox>
      <Typography variant="h4" gutterBottom>
        Admin - Gerenciar Filmes
      </Typography>
      <form noValidate autoComplete="off">
        <TextField
          label="Cartaz"
          name="cartaz"
          value={novoFilme.cartaz}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <TextField
          label="Título"
          name="titulo"
          value={novoFilme.titulo}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <TextField
          label="Descrição"
          name="descricao"
          value={novoFilme.descricao}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <TextField
          label="Categoria"
          name="categoria"
          value={novoFilme.categoria}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <TextField
          label="Elenco"
          name="elenco"
          value={novoFilme.elenco}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="primary" onClick={handleAddFilme} fullWidth>
          Adicionar Filme
        </Button>
      </form>
      <Grid container spacing={2} sx={{ mt: 4 }}>
        {filmes.map(filme => (
          <Grid item xs={12} sm={6} md={4} key={filme.id}>
            <Card>
              <CardMedia
                component="img"
                height="140"
                image={filme.cartaz}
                alt={filme.titulo}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {filme.titulo}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {filme.descricao}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  <strong>Categoria:</strong> {filme.categoria}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  <strong>Elenco:</strong> {filme.elenco}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </CentralizaBox>
  );
};

export default Admin;
