import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, IconButton, Avatar, Button, TextField, Dialog, DialogActions, DialogContent, DialogTitle, Menu, MenuItem } from '@mui/material';
import { Add as AddIcon } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import CentralizaBox from '../componentes/centralizar.jsx';

const Admin = () => {
  const [filmes, setFilmes] = useState([]);
  const [selectedFilme, setSelectedFilme] = useState(null);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const [nome, setNome] = useState('');
  const [cartaz, setCartaz] = useState('');
  const [descricao, setDescricao] = useState('');
  const [categoria, setCategoria] = useState('');
  const [elenco, setElenco] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const user = { nome: 'Admin' }; // Dummy user data
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFilmes = async () => {
      try {
        const response = await axios.get('http://localhost:4000/filmes');
        setFilmes(response.data);
      } catch (error) {
        console.error('Erro ao buscar filmes:', error);
      }
    };

    fetchFilmes();
  }, []);

  const handleLogout = () => {
    navigate('/login');
  };

  const handleAddFilme = async () => {
    try {
      await axios.post('http://localhost:4000/filmes', { nome, cartaz, descricao, categoria, elenco });
      setDialogOpen(false);
      setNome('');
      setCartaz('');
      setDescricao('');
      setCategoria('');
      setElenco('');
      const response = await axios.get('http://localhost:4000/filmes');
      setFilmes(response.data);
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
    }
  };

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const filteredFilmes = filmes.filter((filme) =>
    filme.nome && filme.nome.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <CentralizaBox>
      <AppBar position="static">
        <Toolbar>
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/thumb/0/08/Netflix_2015_logo.svg/320px-Netflix_2015_logo.svg.png"
            alt="Netflix Logo"
            style={{ height: 40, marginRight: 20 }}
          />
          <TextField
            placeholder="Search..."
            variant="outlined"
            size="small"
            style={{ marginRight: 20, flexGrow: 1, width: '300px' }}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Button color="inherit">Filme</Button>
          <Button color="inherit">TV Show</Button>
          <Button color="inherit">Infantil</Button>
          <IconButton color="inherit" onClick={() => setDialogOpen(true)}>
            <AddIcon />
          </IconButton>
          <IconButton color="inherit" onClick={handleClick}>
            <Avatar>{user.nome.charAt(0)}</Avatar>
          </IconButton>
          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem onClick={handleLogout}>Logout</MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', marginTop: 20, padding: '0 20px' }}>
        {filteredFilmes.map((filme) => (
          <div
            key={filme.id}
            style={{ margin: 10, cursor: 'pointer' }}
            onClick={() => setSelectedFilme(filme)}
          >
            <img
              src={filme.cartaz}
              alt={filme.nome}
              style={{ width: 150, height: 225, objectFit: 'cover' }}
            />
            <Typography variant="subtitle1" align="center">{filme.nome}</Typography>
          </div>
        ))}
      </div>
      {selectedFilme && (
        <Dialog open={Boolean(selectedFilme)} onClose={() => setSelectedFilme(null)}>
          <DialogTitle>{selectedFilme.nome}</DialogTitle>
          <DialogContent>
            <Typography variant="body1"><strong>Descrição:</strong> {selectedFilme.descricao}</Typography>
            <Typography variant="body1"><strong>Categoria:</strong> {selectedFilme.categoria}</Typography>
            <Typography variant="body1"><strong>Elenco:</strong> {selectedFilme.elenco}</Typography>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setSelectedFilme(null)} color="primary">Fechar</Button>
          </DialogActions>
        </Dialog>
      )}
      <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
        <DialogTitle>Adicionar Filme</DialogTitle>
        <DialogContent>
          <TextField
            margin="dense"
            label="Nome"
            type="text"
            fullWidth
            value={nome}
            onChange={(e) => setNome(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Cartaz"
            type="text"
            fullWidth
            value={cartaz}
            onChange={(e) => setCartaz(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Descrição"
            type="text"
            fullWidth
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Categoria"
            type="text"
            fullWidth
            value={categoria}
            onChange={(e) => setCategoria(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Elenco"
            type="text"
            fullWidth
            value={elenco}
            onChange={(e) => setElenco(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDialogOpen(false)} color="primary">Cancelar</Button>
          <Button onClick={handleAddFilme} color="primary">Adicionar</Button>
        </DialogActions>
      </Dialog>
    </CentralizaBox>
  );
};

export default Admin;
