import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, List, ListItem, ListItemText, IconButton } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import CentralizaBox from '../componentes/centralizar.jsx';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

const Admin = () => {
  const [filmes, setFilmes] = useState([]);
  const [novoFilme, setNovoFilme] = useState({ titulo: '', descricao: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFilmes = async () => {
      try {
        const response = await axios.get('http://localhost:4000/filmes');
        setFilmes(response.data);
      } catch (error) {
        console.error('Erro ao buscar filmes:', error);
      }
    };

    fetchFilmes();
  }, []);

  const handleAddFilme = async () => {
    try {
      const response = await axios.post('http://localhost:4000/filmes', novoFilme);
      setFilmes([...filmes, response.data]);
      setNovoFilme({ titulo: '', descricao: '' });
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
    }
  };

  const handleDeleteFilme = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/filmes/${id}`);
      setFilmes(filmes.filter((filme) => filme.id !== id));
    } catch (error) {
      console.error('Erro ao deletar filme:', error);
    }
  };

  const handleEditFilme = async (id, updatedFilme) => {
    try {
      const response = await axios.put(`http://localhost:4000/filmes/${id}`, updatedFilme);
      setFilmes(filmes.map((filme) => (filme.id === id ? response.data : filme)));
    } catch (error) {
      console.error('Erro ao editar filme:', error);
    }
  };

  return (
    <CentralizaBox>
      <Typography variant="h5" gutterBottom>
        Administração de Filmes
      </Typography>
      <List>
        {filmes.map((filme) => (
          <ListItem key={filme.id}>
            <ListItemText primary={filme.titulo} secondary={filme.descricao} />
            <IconButton onClick={() => handleEditFilme(filme.id, { titulo: 'Novo Título', descricao: 'Nova Descrição' })}>
              <EditIcon />
            </IconButton>
            <IconButton onClick={() => handleDeleteFilme(filme.id)}>
              <DeleteIcon />
            </IconButton>
          </ListItem>
        ))}
      </List>
      <TextField
        label="Título"
        fullWidth
        margin="normal"
        value={novoFilme.titulo}
        onChange={(e) => setNovoFilme({ ...novoFilme, titulo: e.target.value })}
      />
      <TextField
        label="Descrição"
        fullWidth
        margin="normal"
        value={novoFilme.descricao}
        onChange={(e) => setNovoFilme({ ...novoFilme, descricao: e.target.value })}
      />
      <Button
        variant="contained"
        color="primary"
        fullWidth
        onClick={handleAddFilme}
      >
        Adicionar Filme
      </Button>
    </CentralizaBox>
  );
};

export default Admin;
